mapper.select:
select

from TRANS_DETAIL
where SERIAL_NO = #{serialNo,jdbcType=VARCHAR}
and TRANS_ID = #{transId,jdbcType=VARCHAR}

mapper.delete:
delete from TRANS_DETAIL
where SERIAL_NO = #{serialNo,jdbcType=VARCHAR}
and TRANS_ID = #{transId,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM(SELECT ROWNUM rn, s.* FROM (
select
MERCHANT_ID, TRANS_DATE, TRANS_TYPE, USER_DEFIND_MARK, AMOUNT1, AMOUNT2, AMOUNT3, AMOUNT4,
AMOUNT11, AMOUNT12, AMOUNT13, AMOUNT14
from TRANS_DETAIL




    order by ${orderByClause}

) s WHERE ROWNUM <= (#{offset} + #{limit})) r WHERE r.rn > #{offset}
